package introductionToJava;

public class Methods {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		loops();
		System.out.println("********");
		loops();
		
		

	}
	public static void loops() {
		int i=1;
		while(i<=5) {
			System.out.print(i);
			i++;
		}
		System.out.println("\n");
	}

}
